import React, { Reducer, useEffect, useReducer } from "react";
import "./App.css";
import rover from "./Rover";
import { Command, Direction, Rovers } from "./rover.types";
import * as d3 from "d3";

type State = {
  existingRoversPosition: Rovers[];
  roversLeft: string[];
};

type Action =
  | { type: "loaded_rovers"; payload: string[] }
  | { type: "change_rovers"; payload: Rovers };

function reducer(state: State, action: Action): State {
  switch (action.type) {
    case "loaded_rovers":
      return {
        ...state,
        roversLeft: action.payload,
      };
    case "change_rovers":
      return {
        ...state,
        roversLeft: [...state.roversLeft.slice(1)],
        existingRoversPosition: [
          ...state.existingRoversPosition,
          action.payload,
        ],
      };
  }
}

function App() {
  ///// These will be done in the other file///////
  const maxTerrainSize = { x: 5, y: 5 };

  const [{ existingRoversPosition, roversLeft }, dispatch] = useReducer<
    Reducer<State, Action>
  >(reducer, {
    existingRoversPosition: [],
    roversLeft: [],
  });

  useEffect(() => {
    var csvFilePath = "src/rover_instructions.csv";
    d3.csv(csvFilePath).then(function (csvFile) {
      debugger;
      console.log("min/index.jsx csvFile:");
      console.log("csvfile: " + csvFile);
      console.log(csvFile);
      // console.log("type: " + type(csvFile));
      console.log("min/index.jsx csvFile end");

      return <div className="page-holder"></div>;
    });

    dispatch({
      type: "loaded_rovers",
      payload: ["1 2 N | LMLMLMLMM", "1 2 N | LMLMLMLMR"],
    });
  }, []);

  if (roversLeft.length > 0) {
    const currentRover = roversLeft[0];
    const [startPostion, commands] = currentRover.split("|");
    const [x, y, position] = startPostion.split(" ");
    const mainCommands = commands.split("");
    const finalPostoin = rover(
      maxTerrainSize,
      { x: parseInt(x), y: parseInt(y), position: position as Direction },
      mainCommands as Command[],
      existingRoversPosition
    );
    dispatch({ type: "change_rovers", payload: finalPostoin });
  }

  console.log("existing", existingRoversPosition);
  console.log("left", roversLeft);

  return (
    <div className="App">
      {[...Array(maxTerrainSize.x)].map((e, i) => {
        return (
          <div className="container">
            {[...Array(maxTerrainSize.y)].map((e, j) => {
              const roverHere = existingRoversPosition.find((rover) => {
                return rover.x === i && rover.y === j;
              });
              return roverHere ? (
                <div className="item item2"> {i + " " + j}</div>
              ) : (
                <div className=" item "> {i + " " + j} </div>
              );
            })}
          </div>
        );
      })}
    </div>
  );
}

export default App;
