import React, { Reducer, useEffect, useReducer } from "react";
import "./App.css";
import rover from "./Rover";
import { Command, Direction, Rovers } from "./rover.types";
import Papa from "papaparse";

interface RoversLeft {
  startingPostion: Rovers;
  commands: Command[];
}

type State = {
  existingRoversPosition: Rovers[];
  roversLeft: RoversLeft[];
};

type Action =
  | { type: "loaded_rovers"; payload: RoversLeft[] }
  | { type: "change_rovers"; payload: Rovers };

function reducer(state: State, action: Action): State {
  switch (action.type) {
    case "loaded_rovers":
      return {
        ...state,
        roversLeft: action.payload,
      };
    case "change_rovers":
      return {
        ...state,
        roversLeft: [...state.roversLeft.slice(1)],
        existingRoversPosition: [
          ...state.existingRoversPosition,
          action.payload,
        ],
      };
  }
}
const CSV_ADDRESS = "rover_instructions.csv";
function App() {
  const maxTerrainSize = { x: 5, y: 5 };

  const [{ existingRoversPosition, roversLeft }, dispatch] = useReducer<
    Reducer<State, Action>
  >(reducer, {
    existingRoversPosition: [],
    roversLeft: [],
  });

  useEffect(() => {
    async function fetchCsv() {
      const response = await fetch(CSV_ADDRESS);

      if (response.body != null) {
        const reader = response.body.getReader();
        const result = await reader.read();
        const decoder = new TextDecoder("utf-8");
        const csv = await decoder.decode(result.value);

        Papa.parse(csv, {
          skipEmptyLines: true,

          complete: (data: any) => {
            const aa = data.data.map((data: any) => {
              const [x, y, position] = data[0].substring(1).split(" ");
              const mainCommands = data[1].slice(1, -1).split("");

              return {
                startingPostion: {
                  x: parseInt(x),
                  y: parseInt(y),
                  position: position as Direction,
                },
                commands: mainCommands as Command[],
              };
            });
            dispatch({
              type: "loaded_rovers",
              payload: aa as RoversLeft[],
            });
          },
        });
      }
    }
    fetchCsv();
  }, []);

  useEffect(() => {
    //After Processing the first Rover we can move on to the next one
    if (roversLeft.length > 0) {
      const currentRover = roversLeft[0];

      const finalPostoin = rover(
        maxTerrainSize,
        currentRover.startingPostion,
        currentRover.commands,
        existingRoversPosition
      );
      dispatch({ type: "change_rovers", payload: finalPostoin });
    }
  }, [roversLeft, existingRoversPosition, maxTerrainSize]);

  return (
    <div className="App">
      {[...Array(maxTerrainSize.x)].map((e, i) => {
        return (
          <div className="container" key={`row-${i}`}>
            {[...Array(maxTerrainSize.y)].map((e, j) => {
              const roverHere = existingRoversPosition.findIndex((rover) => {
                return rover.x === i && rover.y === j;
              });
              return roverHere !== -1 ? (
                <div className="item item2" key={`row-${i}-col-${j}`}>
                  Rover {roverHere + 1}
                </div>
              ) : (
                <div className=" item " key={`row-${i}-col-${j}`}>
                  &nbsp;
                </div>
              );
            })}
          </div>
        );
      })}
    </div>
  );
}

export default App;
