import React, { Dispatch, Reducer, useEffect, useReducer } from "react";
import "./App.css";
import rover from "./Rover";
import { Command, Direction, Rovers, MaxTerrainSize } from "./rover.types";
import Papa from "papaparse";

interface UndeployedRover {
  initialPosition: Rovers;
  commands: Command[];
}

interface Data {
  data: string[][];
}
type State = {
  existingRoversPosition: Rovers[];
  roversLeft: UndeployedRover[];
};

async function fetchCsv(dispatch: Dispatch<Action>, csvAddress: string) {
  const response = await fetch(csvAddress);

  if (response.body != null) {
    const reader = response.body.getReader();
    const result = await reader.read();
    const decoder = new TextDecoder("utf-8");
    const csv = await decoder.decode(result.value);

    Papa.parse(csv, {
      skipEmptyLines: true,
      complete: ({ data }: Data) => {
        const processedRovers = data.map((data) => {
          const [x, y, direction] = data[0].substring(1).split(" ");
          const mainCommands = data[1].slice(1, -1).split("");

          return {
            initialPosition: {
              x: parseInt(x),
              y: parseInt(y),
              direction: direction as Direction,
            },
            commands: mainCommands as Command[],
          };
        });
        dispatch({
          type: "loaded_rovers",
          payload: processedRovers as UndeployedRover[],
        });
      },
    });
  }
}

type Action =
  | { type: "loaded_rovers"; payload: UndeployedRover[] }
  | { type: "change_rovers"; payload: Rovers };

function reducer(state: State, action: Action): State {
  switch (action.type) {
    case "loaded_rovers":
      return {
        ...state,
        roversLeft: action.payload,
      };
    case "change_rovers":
      return {
        ...state,
        roversLeft: [...state.roversLeft.slice(1)],
        existingRoversPosition: [
          ...state.existingRoversPosition,
          action.payload,
        ],
      };
  }
}

interface RoversGridProps {
  maxTerrainSize: MaxTerrainSize;
  csvAddress: string;
}

function RoversGrid({ maxTerrainSize, csvAddress }: RoversGridProps) {
  const [{ existingRoversPosition, roversLeft }, dispatch] = useReducer<
    Reducer<State, Action>
  >(reducer, {
    existingRoversPosition: [],
    roversLeft: [],
  });

  useEffect(() => {
    fetchCsv(dispatch, csvAddress);
  }, [csvAddress]);

  useEffect(() => {
    if (roversLeft.length > 0) {
      const currentRover = roversLeft[0];

      const roverFinalPosition = rover(
        maxTerrainSize,
        currentRover.initialPosition,
        currentRover.commands,
        existingRoversPosition
      );

      dispatch({ type: "change_rovers", payload: roverFinalPosition });
    }
  }, [existingRoversPosition, maxTerrainSize, roversLeft]);

  return (
    <div className="App">
      {[...Array(maxTerrainSize.x)].map((e, i) => {
        return (
          <div className="container" key={`row-${i}`}>
            {[...Array(maxTerrainSize.y)].map((e, j) => {
              const roverHere = existingRoversPosition.findIndex((rover) => {
                return rover.x === i && rover.y === j;
              });
              return roverHere !== -1 ? (
                <div className="item item2" key={`row-${i}-col-${j}`}>
                  Rover {roverHere + 1}
                </div>
              ) : (
                <div className=" item " key={`row-${i}-col-${j}`}>
                  &nbsp;
                </div>
              );
            })}
          </div>
        );
      })}
    </div>
  );
}

export default RoversGrid;
