import React, { Reducer, useReducer, useState } from "react";
import logo from "./logo.svg";
import "./App.css";

/// is on map
// is valid position -- other rover there

type Rovers = {
  x: number;
  y: number;
  position: string;
};

type State = {
  existingRoversPosition: Rovers[];
  currentRoverPosition: Rovers;
};

type Action =
  | { type: "turnLeft" }
  | { type: "turnRight" }
  | { type: "MoveForward" };

function reducer(state: State, action: Action): State {
  switch (action.type) {
    case "turnLeft":
      return { isLoading: true };
    case "turnRight":
      return { isLoading: false, data: action.results };
    case "MoveForward":
      return { isLoading: false, error: action.error };
  }
}
function App() {
  const input = "1 2 N | LMLMLMLMM";

  const [startPostion, commands] = input.split("|");

  const [x, y, position] = startPostion.split(" ");

  const [{ existingRoversPosition, currentRoverPosition }, dispatch] =
    useReducer<Reducer<State, Action>>(reducer, {
      existingRoversPosition: [
        {
          x: 1,
          y: 2,
          position: "N",
        },
      ],
      currentRoverPosition: {
        x: parseInt(x),
        y: parseInt(y),
        position: position,
      },
    });

  commands.split("").forEach((c) => {
    if (c === "L") {
      dispatch({ type: "turnLeft" });
    } else if (c === "R") {
      dispatch({ type: "turnRight" });
    } else if (c === "M") {
      dispatch({ type: "moveForward" });
    }
  });
  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          Edit <code>src/App.tsx</code> and save to reload.
        </p>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
      </header>
    </div>
  );
}

export default App;
