import { Command, Direction, Rovers } from "./rover.types";

const commandLeft = (direction: Direction) => {
  switch (direction) {
    case Direction.NORTH:
      return Direction.WEST;
    case Direction.SOUTH:
      return Direction.EAST;
    case Direction.EAST:
      return Direction.NORTH;
    case Direction.WEST:
      return Direction.SOUTH;
    default:
      return direction;
  }
};

const commandRight = (direction: Direction) => {
  switch (direction) {
    case Direction.NORTH:
      return Direction.EAST;
    case Direction.SOUTH:
      return Direction.WEST;
    case Direction.EAST:
      return Direction.SOUTH;
    case Direction.WEST:
      return Direction.NORTH;
    default:
      return direction;
  }
};

const commandForward = (currentRoverPosition: Rovers) => {
  switch (currentRoverPosition.position) {
    case Direction.NORTH:
      return {
        ...currentRoverPosition,
        y: currentRoverPosition.y + 1,
      };
    case Direction.SOUTH:
      return {
        ...currentRoverPosition,
        y: currentRoverPosition.y - 1,
      };
    case Direction.EAST:
      return {
        ...currentRoverPosition,
        x: currentRoverPosition.x + 1,
      };
    case Direction.WEST:
      return {
        ...currentRoverPosition,
        x: currentRoverPosition.x + 1,
      };
    default:
      return { ...currentRoverPosition };
  }
};

const isOnMap = (state: Rovers, bounds: { x: number; y: number }) => {
  const newLocation = commandForward(state);

  const xmax = newLocation.x <= bounds.x && newLocation.x >= 0;
  const ymax = newLocation.y <= bounds.y && newLocation.y >= 0;

  return ymax && xmax;
};

const spaceIsEmpty = (
  existingRoversPosition: Rovers[],
  currentRoverPosition: Rovers
) => {
  const newLocation = commandForward(currentRoverPosition);

  const roverInSpot = existingRoversPosition.find((rover) => {
    return rover.x === newLocation.x && rover.y === newLocation.y;
  });
  return !roverInSpot;
};

interface RoverProps {
  maxTerrainSize: {
    x: number;
    y: number;
  };
  startPosition: Rovers;
  commands: Command[];
  existingRovers: Rovers[];
}
function Rover(
  maxTerrainSize: {
    x: number;
    y: number;
  },
  startPosition: Rovers,
  commands: Command[],
  existingRovers: Rovers[]
) {
  let position = startPosition;
  commands.forEach((c) => {
    if (c === Command.TURN_LEFT) {
      position = {
        ...position,
        position: commandLeft(position.position),
      };
    } else if (c === Command.TURN_RIGHT) {
      position = {
        ...position,
        position: commandRight(position.position),
      };
    } else if (c === Command.MOVE_FORWARD) {
      if (
        isOnMap(position, maxTerrainSize) &&
        spaceIsEmpty(existingRovers, position)
      ) {
        position = {
          ...commandForward(position),
        };
      }
    }
  });

  return position;
}

export default Rover;
