import React, { Reducer, useReducer, useState } from "react";
import logo from "./logo.svg";
import "./App.css";

/// is on map
// is valid position -- other rover there
const commandLeft = (direction: string) => {
  switch (direction) {
    case "N":
      return "W";
    case "S":
      return "E";
    case "E":
      return "N";
    case "w":
      return "S";
    default:
      return direction;
  }
};

const commandRight = (direction: string) => {
  switch (direction) {
    case "N":
      return "E";
    case "S":
      return "W";
    case "E":
      return "S";
    case "w":
      return "N";
    default:
      return direction;
  }
};

const commandForward = (currentRoverPosition: Rovers) => {
  switch (currentRoverPosition.position) {
    case "N":
      return {
        ...currentRoverPosition,
        y: currentRoverPosition.y + 1,
      };
    case "S":
      return {
        ...currentRoverPosition,

        y: currentRoverPosition.y - 1,
      };
    case "E":
      return {
        ...currentRoverPosition,

        x: currentRoverPosition.x + 1,
      };
    case "w":
      return {
        ...currentRoverPosition,

        x: currentRoverPosition.x + 1,
      };
    default:
      return currentRoverPosition;
  }
};

type Rovers = {
  x: number;
  y: number;
  position: string;
};

type State = {
  existingRoversPosition: Rovers[];
  currentRoverPosition: Rovers;
};

type Action =
  | { type: "turnLeft" }
  | { type: "turnRight" }
  | { type: "moveForward" };

function reducer(state: State, action: Action): State {
  switch (action.type) {
    case "turnLeft":
      return {
        ...state,
        currentRoverPosition: {
          ...state.currentRoverPosition,
          position: commandLeft(state.currentRoverPosition.position),
        },
      };
    case "turnRight":
      return {
        ...state,
        currentRoverPosition: {
          ...state.currentRoverPosition,
          position: commandRight(state.currentRoverPosition.position),
        },
      };
    case "moveForward":
      return {
        ...state,
        currentRoverPosition: {
          ...commandForward(state.currentRoverPosition),
        },
      };
  }
}

const isOnMap = (state: Rovers, bounds: { x: number; y: number }) => {
  const newLocation = commandForward(state);

  const xmax = newLocation.x <= bounds.x && newLocation.x >= 0;
  const ymax = newLocation.y <= bounds.y && newLocation.y >= 0;

  return ymax && xmax;
};

const spaceIsEmpty = (
  existingRoversPosition: Rovers[],
  currentRoverPosition: Rovers
) => {
  const newLocation = commandForward(currentRoverPosition);

  /// loop over and see if any of the other ones are there

  const roverInSpot = existingRoversPosition.find((rover) => {
    return rover.x === newLocation.x && rover.y === newLocation.y;
  });
  return !roverInSpot;
};
function App() {
  ///// These will be done in the other file///////
  const input = "1 2 N | LMLMLMLMM";
  const maxTerrainSize = { x: 5, y: 5 };
  const [startPostion, commands] = input.split("|");
  const [x, y, position] = startPostion.split(" ");
  const mainCommands = commands.split("");

  /// Really where it kicks off ///////
  const [{ existingRoversPosition, currentRoverPosition }, dispatch] =
    useReducer<Reducer<State, Action>>(reducer, {
      existingRoversPosition: [
        {
          x: 1,
          y: 2,
          position: "N",
        },
      ],
      currentRoverPosition: {
        x: parseInt(x),
        y: parseInt(y),
        position: position,
      },
    });

  mainCommands.forEach((c) => {
    if (c === "L") {
      dispatch({ type: "turnLeft" });
    } else if (c === "R") {
      dispatch({ type: "turnRight" });
    } else if (c === "M") {
      if (
        isOnMap(currentRoverPosition, maxTerrainSize) &&
        spaceIsEmpty(currentRoverPosition, existingRoversPosition)
      ) {
        dispatch({ type: "moveForward" });
      }
    }
  });

  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          Edit <code>src/App.tsx</code> and save to reload.
        </p>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
      </header>
    </div>
  );
}

export default App;
